            "grid": $json.raw_data.days.map(d => ({
                "day": d.day_index,
                // Server V8 c·∫ßn delta th√¥ (s·ªë th·ª±c), n·∫øu ch∆∞a nh·∫≠p th√¨ g·ª≠i null
