parameters.jsCode:
function toNum(v) {
  if (v === null || v === undefined) return null;
  const s = String(v).trim();
  if (!s) return null;
  const n = Number(s.replace(",", "."));
  return Number.isFinite(n) ? n : null;
}

function pickLastNonNull(arr) {
  for (let i = arr.length - 1; i >= 0; i--) if (arr[i] !== null) return arr[i];
  return null;
}

return items.map(item => {
  const j = item.json;

  const name = j.player_name ?? j["Ng∆∞·ªùi ch∆°i"] ?? "";
  const team = j.team ?? j["ƒê·ªôi"] ?? "";
  const avatar_url = j.avatar_url ?? j.avatar ?? j["avatar"] ?? null;

  // ========================================================
  // üîß FALLBACK LOGIC FOR CAPTAIN ID
  // ========================================================

  // Team-to-Captain ID mapping (fallback when Google Sheets missing data)
  const TEAM_CAPTAIN_FALLBACK = {
    "ƒê·ªôi B√°o SƒÉn": "3633624730889700356",
    "ƒê·ªôi C√°nh Gi√≥": "2405161230734177",  // Update with actual captain ID
    "ƒê·ªôi S√≥ng D≈©ng": "2405161230734177", // Update with actual captain ID
    // Add more teams as needed
  };

  // Ultimate fallback (used when team not in mapping)
  const DEFAULT_CAPTAIN_ID = "2405161230734177";

  // Try to get captain ID from Google Sheets first
  let zaloID_captain = j.zaloID_captain ?? j["zaloID_captain"] ?? null;

  // If empty string, treat as null
  if (zaloID_captain === "") {
    zaloID_captain = null;
  }

  // Apply fallback logic
  if (!zaloID_captain) {
    // Try team-specific fallback
    zaloID_captain = TEAM_CAPTAIN_FALLBACK[team] ?? DEFAULT_CAPTAIN_ID;
    console.warn(`‚ö†Ô∏è FALLBACK: Using fallback captain ID for ${name} (${team}): ${zaloID_captain}`);
  }

  // Final validation
  if (!zaloID_captain) {
    console.error(`‚ùå ERROR: No captain ID available for ${name} (${team})`);
  }

  const round_name = j["V√≤ng"] ?? j.round_name ?? "Marathon";
  const round_range_raw = j["Th·ªùi Gian"] ?? j.time ?? j.time_range ?? "";
  const round_range = typeof round_range_raw === "string"
    ? round_range_raw.replace("-", " ‚Äì ")
    : "";

  const weights = [];
  for (let d = 0; d <= 10; d++) {
    const key1 = `day${d}`;
    const key2 = `Day ${d}`;
    const val = (j[key1] !== undefined) ? j[key1] : j[key2];
    weights.push(toNum(val));
  }

  const startWeight = weights[0];
  const currentWeight = pickLastNonNull(weights);
  const deltaWeight =
    (startWeight !== null && currentWeight !== null)
      ? (currentWeight - startWeight)
      : null;

  const is_finished = weights[10] !== null;

  const grid = [];
  for (let day = 1; day <= 10; day++) {
    const w = weights[day];
    grid.push({
      day,
      delta_from_start:
        (startWeight !== null && w !== null) ? (w - startWeight) : null
    });
  }

  const data_for_template = {
    player: {
      name,
      team,
      avatar: avatar_url,
      round_name,
      round_range,
      info_line: j.info_line ?? j["info_line"] ?? "",
      grid
    },
    stats: {
      start_weight: startWeight,
      current_weight: currentWeight,
      delta_weight: deltaWeight
    },
    is_finished
  };

  // NEW ‚Äî force info_line = round_range if info_line empty
  if (!data_for_template.player.info_line || data_for_template.player.info_line.trim() === "") {
    data_for_template.player.info_line = round_range;
  }

  const raw_days = [];
  for (let day = 1; day <= 10; day++) {
    const w = weights[day];
    raw_days.push({
      day_index: day,
      is_future: w === null,
      daily_delta: (startWeight !== null && w !== null) ? (w - startWeight) : null,
      current_weight: w
    });
  }

  return {
    json: {
      zalo_captain_id: zaloID_captain,  // ‚úÖ With fallback logic
      zalo_user_id: zaloID_captain,     // Legacy compatibility
      avatar_url: avatar_url,
      text_report: `üìä B√ÅO C√ÅO TI·∫æN TR√åNH
üë• ƒê·ªôi: ${team}
üë§ Th√†nh vi√™n: ${name}
üìÖ ${data_for_template.player.info_line}
-------------------
üí° ƒêang t·∫°o ·∫£nh b√°o c√°o...`,
      raw_data: {
        days: raw_days,
        stats: {
          start: startWeight,
          finish: weights[10] ?? null,
          current_change: deltaWeight
        },
        player: {
          name,
          team,
          avatar: avatar_url,
          round_name,
          info_line: data_for_template.player.info_line
        }
      },
      render_request: {
        template: j.template ?? "personal_progress",
        data: data_for_template,
        width: 1080,
        height: 1444,
        filename_prefix: "personal"
      }
    }
  };
});
